// Dashboard.jsx
import React, { useEffect, useState } from "react";
import { auth, db } from "../firebase";
import { onAuthStateChanged } from "firebase/auth";
import { collection, getDocs, query, where } from "firebase/firestore";

const Dashboard = () => {
    const [currentUserEmail, setCurrentUserEmail] = useState("");
    const [currentUserCompany, setCurrentUserCompany] = useState(null);
    const [companies, setCompanies] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");
    const [blueprint, setBlueprint] = useState("");

    // Get current user email
    useEffect(() => {
        const unsubscribe = onAuthStateChanged(auth, (user) => {
            if (user) setCurrentUserEmail(user.email);
            else setCurrentUserEmail("");
        });
        return () => unsubscribe();
    }, []);

    // Fetch all companies and current user's company info
    useEffect(() => {
        const fetchCompanies = async () => {
            try {
                const querySnapshot = await getDocs(collection(db, "companies"));
                const companyList = [];
                let currentCompany = null;

                querySnapshot.forEach((doc) => {
                    const data = doc.data();
                    const company = { id: doc.id, ...data };
                    companyList.push(company);

                    if (data.email === currentUserEmail) {
                        currentCompany = company;
                    }
                });

                setCompanies(companyList);
                setCurrentUserCompany(currentCompany);
            } catch (err) {
                console.error("Error fetching companies:", err);
                setError("Failed to load companies.");
            }
        };

        if (currentUserEmail) fetchCompanies();
    }, [currentUserEmail]);

    // Check if two companies have a correlation
    const hasCorrelation = (companyA, companyB) => {
        if (!companyA || !companyB) return false;

        const aProducesBNeeds = companyA.produces?.some((item) =>
            companyB.needs?.includes(item)
        );
        const bProducesANeeds = companyB.produces?.some((item) =>
            companyA.needs?.includes(item)
        );

        return aProducesBNeeds || bProducesANeeds;
    };

    // Generate blueprint handler
    const handleGenerateBlueprint = async (targetCompany) => {
        if (!currentUserCompany || !targetCompany) {
            setError("Missing company information.");
            return;
        }

        setLoading(true);
        setError("");
        setBlueprint("");

        try {
            const response = await fetch("http://localhost:5000/generateBlueprint", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({
                    companyName: targetCompany.name || targetCompany.email.split("@")[0],
                    description: `Collaboration between ${currentUserCompany.name} and ${targetCompany.name}`,
                    goals: `Exchange waste and resources between ${currentUserCompany.name} and ${targetCompany.name}`,
                    companyEmails: [currentUserCompany.email, targetCompany.email],
                }),
            });

            const data = await response.json();

            if (!response.ok) {
                setError(data.error || "Failed to generate blueprint.");
            } else if (data.blueprint) {
                setBlueprint(data.blueprint);
            } else {
                setError("Blueprint generation failed: No content returned.");
            }
        } catch (err) {
            console.error("Error generating blueprint:", err);
            setError("Network or server error. Please try again.");
        } finally {
            setLoading(false);
        }
    };

    if (!currentUserEmail) return <p>Please sign in to view dashboard.</p>;

    return (
        <div style={{ padding: "20px" }}>
            <h1>Dashboard</h1>
            {error && <p style={{ color: "red" }}>{error}</p>}

            <div style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
                {companies
                    .filter((c) => c.email !== currentUserEmail)
                    .map((company) => {
                        const enabled = hasCorrelation(currentUserCompany, company);
                        return (
                            <div
                                key={company.id}
                                style={{
                                    border: "1px solid #ccc",
                                    padding: "15px",
                                    borderRadius: "10px",
                                    width: "250px",
                                    backgroundColor: enabled ? "#fff" : "#f5f5f5",
                                }}
                            >
                                <h3>{company.name}</h3>
                                <p>Email: {company.email}</p>
                                <button
                                    onClick={() => handleGenerateBlueprint(company)}
                                    disabled={loading || !enabled}
                                    style={{
                                        padding: "8px 12px",
                                        borderRadius: "5px",
                                        backgroundColor: enabled ? "#4caf50" : "#999",
                                        color: "#fff",
                                        border: "none",
                                        cursor:
                                            loading || !enabled ? "not-allowed" : "pointer",
                                        opacity: loading || !enabled ? 0.5 : 1,
                                    }}
                                >
                                    {loading ? "Generating..." : "Generate Blueprint"}
                                </button>
                                {!enabled && (
                                    <p style={{ fontSize: "12px", color: "#555" }}>
                                        No matching resources/waste to exchange.
                                    </p>
                                )}
                            </div>
                        );
                    })}
            </div>

            {blueprint && (
                <div
                    style={{
                        marginTop: "20px",
                        padding: "15px",
                        border: "1px solid #aaa",
                        borderRadius: "10px",
                        maxHeight: "400px",
                        overflowY: "auto",
                        backgroundColor: "#f9f9f9",
                        whiteSpace: "pre-wrap",
                    }}
                >
                    <h2>Generated Blueprint</h2>
                    <pre>{blueprint}</pre>
                </div>
            )}
        </div>
    );
};

export default Dashboard;
