// NodalGraph.jsx
import React, { useState, useCallback, useMemo } from "react";
import ReactFlow, {
    ReactFlowProvider,
    Background,
    Controls,
    useNodesState,
    useEdgesState,
} from "reactflow";
import "reactflow/dist/style.css";

const NodalGraph = ({ companies, currentUserEmail }) => {
    const [selectedNode, setSelectedNode] = useState(null);

    // Initialize nodes
    const initialNodes = useMemo(() => companies.map((c, i) => ({
        id: c.id.toString(),
        position: c.position || { x: i * 200, y: i * 150 },
        data: {
            label: c.name,
            email: c.email,
            produces: Array.isArray(c.produces) ? c.produces : [],
            needs: Array.isArray(c.needs) ? c.needs : [],
        },
        style: {
            background: c.email === currentUserEmail ? "red" : "#fff",
            color: "#000",
            border: "2px solid #222",
            width: 150,
            height: 50,
            borderRadius: 50,
            textAlign: "center",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            cursor: "pointer",
        },
    })), [companies, currentUserEmail]);

    const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);

    // Create edges based on company produces/needs correlation
    const initialEdges = useMemo(() => {
        const edges = [];
        companies.forEach((c1) => {
            companies.forEach((c2) => {
                if (
                    c1.needs?.some((need) =>
                        Array.isArray(c2.produces) && c2.produces.includes(need)
                    )
                ) {
                    edges.push({
                        id: `e${c1.id}-${c2.id}`,
                        source: c2.id.toString(),
                        target: c1.id.toString(),
                        animated: true,
                        style: { stroke: "#888" },
                    });
                }
            });
        });
        return edges;
    }, [companies]);

    const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);

    const onNodeClick = useCallback((event, node) => {
        setSelectedNode(node);
    }, []);

    return (
        <ReactFlowProvider>
            <div style={{ width: "100%", height: "80vh", position: "relative" }}>
                <ReactFlow
                    nodes={nodes}
                    edges={edges}
                    onNodesChange={onNodesChange}
                    onEdgesChange={onEdgesChange}
                    onNodeClick={onNodeClick}
                    fitView
                >
                    <Background color="#aaa" gap={16} />
                    <Controls />
                </ReactFlow>

                {/* Bubble for selected node */}
                {selectedNode && (
                    <div
                        style={{
                            position: "absolute",
                            left: selectedNode.position.x + 160, // offset to the right
                            top: selectedNode.position.y,
                            background: "#fff",
                            padding: "10px",
                            borderRadius: "8px",
                            border: "1px solid #222",
                            boxShadow: "0px 0px 8px rgba(0,0,0,0.2)",
                            zIndex: 10,
                            width: 200,
                        }}
                    >
                        <strong>{selectedNode.data.label}</strong>
                        <div>Email: {selectedNode.data.email || "N/A"}</div>
                        <div>
                            Produces:{" "}
                            {Array.isArray(selectedNode.data.produces)
                                ? selectedNode.data.produces.join(", ")
                                : "N/A"}
                        </div>
                        <div>
                            Needs:{" "}
                            {Array.isArray(selectedNode.data.needs)
                                ? selectedNode.data.needs.join(", ")
                                : "N/A"}
                        </div>
                    </div>
                )}
            </div>
        </ReactFlowProvider>
    );
};

export default NodalGraph;
