import React, { useState, useCallback } from "react";
import ReactFlow, { Background, Controls, useNodesState, useEdgesState } from "reactflow";
import "reactflow/dist/style.css";

export default function NodalGraph({ companies, currentUserEmail }) {
    // Transform companies into nodes
    const initialNodes = companies.map((c, idx) => ({
        id: c.email,
        data: { label: c.name },
        position: { x: 100 + idx * 200, y: 100 + (idx % 2) * 200 }, // simple layout
        style: {
            background: c.email === currentUserEmail ? "red" : "#1A192B",
            color: "#fff",
            border: "1px solid #222",
            width: 150,
        },
    }));

    const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
    const [edges, setEdges, onEdgesChange] = useEdgesState([]);
    const [selectedNode, setSelectedNode] = useState(
        nodes.find((n) => n.id === currentUserEmail) || null
    );

    // Handle node click
    const onNodeClick = useCallback((event, node) => {
        setSelectedNode(node);
    }, []);

    // Update selected node position on drag
    const onNodeDragStop = useCallback((event, node) => {
        setNodes((nds) =>
            nds.map((n) => (n.id === node.id ? { ...n, position: node.position } : n))
        );
        if (selectedNode?.id === node.id) {
            setSelectedNode({ ...node });
        }
    }, [selectedNode]);

    return (
        <div style={{ width: "100%", height: "600px", position: "relative" }}>
            <ReactFlow
                nodes={nodes}
                edges={edges}
                onNodesChange={onNodesChange}
                onEdgesChange={onEdgesChange}
                onNodeClick={onNodeClick}
                onNodeDragStop={onNodeDragStop}
                fitView
            >
                <Background variant="dots" gap={12} size={1} />
                <Controls />
            </ReactFlow>

            {/* Bubble for selected node */}
            {selectedNode && (
                <div
                    style={{
                        position: "absolute",
                        top: selectedNode.position.y + 50, // adjust offset
                        left: selectedNode.position.x + 50,
                        padding: "8px 12px",
                        background: "#fff",
                        border: "1px solid #222",
                        borderRadius: "8px",
                        pointerEvents: "none",
                        zIndex: 10,
                    }}
                >
                    <strong>{selectedNode.data.label}</strong>
                    <div>Email: {selectedNode.id}</div>
                </div>
            )}
        </div>
    );
}
