import React, { useState, useEffect } from "react";
import Blueprints from "./Blueprints";
import NodalGraph from "./NodalGraph";

export default function Dashboard({ user, companies = [] }) {
    const [currentCompany, setCurrentCompany] = useState(null);
    const [selectedCompany, setSelectedCompany] = useState(null);
    const [blueprintOpen, setBlueprintOpen] = useState(false);
    const [loading, setLoading] = useState(false);       // for showing loading spinner
    const [error, setError] = useState("");              // to display errors
    const [blueprint, setBlueprint] = useState("");      // to store the generated blueprint

    useEffect(() => {
        if (!user) return;

        // Find the company corresponding to logged-in user
        const myCompany = companies.find((c) => c.email === user.email);
        setCurrentCompany(myCompany || null);
    }, [user, companies]);

    if (!currentCompany) {
        return <p>Loading your company data... Please add your company if missing.</p>;
    }

    const otherCompanies = companies.filter(
        (c) => c.email && c.email !== currentCompany.email
    );

    const handleGenerateBlueprint = async (targetCompanyEmail) => {
        if (!targetCompanyEmail) {
            setError("Target company not specified.");
            return;
        }

        setLoading(true);
        setError("");
        setBlueprint("");

        try {
            const response = await fetch("http://localhost:5000/generateBlueprint", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({
                    companyName: targetCompanyEmail.split("@")[0] || "Unnamed Company",
                    description: `Collaboration with ${targetCompanyEmail}`,
                    goals: `Exchange waste and resources between ${currentUserEmail} and ${targetCompanyEmail}`,
                    companyEmails: [currentUserEmail, targetCompanyEmail],
                }),
            });

            const data = await response.json();

            if (!response.ok) {
                setError(data.error || "Failed to generate blueprint.");
            } else if (data.blueprint) {
                setBlueprint(data.blueprint);
            } else {
                setError("Blueprint generation failed: No content returned.");
            }
        } catch (err) {
            console.error("Error generating blueprint:", err);
            setError("Network or server error. Please try again.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="p-6">
            <h2 className="text-3xl font-bold mb-4">Welcome, {user?.displayName}</h2>

            <h3 className="text-xl font-semibold mb-4">
                Your Company: {currentCompany.name || currentCompany.email}
            </h3>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mb-6">
                {otherCompanies.map((company) => (
                    <div key={company.id || company.email} className="border rounded p-4 shadow bg-white">
                        <h4 className="font-semibold mb-2">{company.name || company.email}</h4>
                        <p><strong>Needs:</strong> {company.needs || "N/A"}</p>
                        <p><strong>Produces:</strong> {company.waste || "N/A"}</p>
                        {currentCompany && company ? (
                            <button
                                onClick={() => handleGenerateBlueprint(company)}
                                className="mt-2 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 transition"
                            >
                                Generate Blueprint
                            </button>
                        ) : null}
                    </div>
                ))}
            </div>

            <NodalGraph nodes={companies} edges={[]} />

            {blueprintOpen && selectedCompany && (
                <Blueprints
                    currentCompany={currentCompany}
                    targetCompany={selectedCompany}
                    onClose={() => setBlueprintOpen(false)}
                />
            )}
        </div>
    );
}
